insert into dnd_rulebook (`id`, `dnd_edition_id`, `name`, `abbr`, `description`, `year`, `official_url`, `slug`, `image`, `published`) values (33, '6', 'Unapproachable East', 'Una', 'Tales from beyond the Easting Reach are told with awed voices and hushed tones. Known to most of Faerun as the homeland of the Simbul, the hathrans, and the Red Wizards, the Unapproachable East is filled with dark secrets, insidious plots, and untold adventure. Discover the people, politics, cities, and societies of the region, along with the monsters, nefarious organizations, and other perils that await unwary travelers in this treacherous corner of the Forgotten Realms game setting.', '2003', '', 'unapproachable-east', '', '2003-05-01');
