insert into dnd_rulebook (`id`, `dnd_edition_id`, `name`, `abbr`, `description`, `year`, `official_url`, `slug`, `image`, `published`) values (48, '7', 'Song and Silence: A Guidebook to Bards and Rogues', 'SaS', 'Finesse and Versatility Make Powerful Allies
